    public Cursor listGames(){
        SQLiteDatabase db = this.getReadableDatabase();

        Cursor cursor = db.query(
                GAME_TBL_NAME,
                new String[]{COL_ID, COL_NAME, COL_GENRE, COL_RELEASE, COL_BLURB},
                    null, null, null, null, null);
        return cursor;
    }

    public Cursor searchLibrary(String query){
        SQLiteDatabase db = this.getReadableDatabase();

        Cursor cursor = db.query(
                GAME_TBL_NAME,
                GAME_COLUMNS,
//Commenting out ID and BLURB so that walls of text and random numbers aren't searchable.
                //COL_ID + " LIKE ? OR " +
                COL_NAME + " LIKE ? OR "
                + COL_GENRE + " LIKE ? OR "
                + COL_RELEASE + " LIKE ? ",
                //+ COL_BLURB + " LIKE ?",
                new String[]{
//                        "%" + query + "%",
//                        "%" + query + "%",
                        "%" + query + "%",
                        "%" + query + "%",
                        "%" + query + "%"},
                        null, null, null, null);

        //import android.database.DatabaseUtils; //uncomment, append to top for console debug
        //DatabaseUtils.dumpCursor(cursor); //uncomment for console debug
        return cursor;
        }

